definition.script:
node('master') {

	def outputDir = "/var/www/vsc-builds/"+"${branch}"+"-all/"

    stage('ENV setup') {
        env.JAVA_HOME="${tool name: 'Java 8.121', type: 'jdk'}"
        env.PATH="${env.JAVA_HOME}/bin:${env.PATH}"
    }

    stage('Clean') {
        sh 'sudo rm * -rf'
    }

    stage('Git clone') {

        sh 'mkdir -p osc-core'
        dir ('osc-core') {
    		try {
    			git branch: '${branch}', credentialsId: 'oscservice-token', url: 'https://github.com/opensecuritycontroller/osc-core.git'
    		} catch (err) {
                git branch: 'master', credentialsId: 'oscservice-token', url: 'https://github.com/opensecuritycontroller/osc-core.git'
            }
        }

    	sh 'mkdir -p security-mgr-api'
        dir ('security-mgr-api') {
    		try {
    			git branch: '${plugin_branch}', credentialsId: 'oscservice-token', url: 'https://github.com/opensecuritycontroller/security-mgr-api.git'
    		} catch (err) {
                git branch: 'master', credentialsId: 'oscservice-token', url: 'https://github.com/opensecuritycontroller/security-mgr-api.git'
            }
        }

    	sh 'mkdir -p sdn-controller-api'
        dir ('sdn-controller-api') {
    		try {
    			git branch: '${plugin_branch}', credentialsId: 'oscservice-token', url: 'https://github.com/opensecuritycontroller/sdn-controller-api.git'
    		} catch (err) {
                git branch: 'master', credentialsId: 'oscservice-token', url: 'https://github.com/opensecuritycontroller/sdn-controller-api.git'
            }
        }

		sh 'mkdir -p security-mgr-sample-plugin'
        dir ('security-mgr-sample-plugin') {
    		try {
    			git branch: '${plugin_branch}', credentialsId: 'oscservice-token', url: 'https://github.com/opensecuritycontroller/security-mgr-sample-plugin.git'
    		} catch (err) {
                git branch: 'master', credentialsId: 'oscservice-token', url: 'https://github.com/opensecuritycontroller/security-mgr-sample-plugin.git'
            }
        }

		sh 'mkdir -p osc-pan-plugin'
        dir ('osc-pan-plugin') {
    		try {
    			git branch: '${plugin_branch}', credentialsId: 'oscservice-token', url: 'https://github.com/opensecuritycontroller/osc-pan-plugin.git'
    		} catch (err) {
                git branch: 'master', credentialsId: 'oscservice-token', url: 'https://github.com/opensecuritycontroller/osc-pan-plugin.git'
            }
        }

    	sh 'mkdir -p sdn-controller-nsc-plugin'
        dir ('sdn-controller-nsc-plugin') {
    		try {
    			git branch: '${plugin_branch}', credentialsId: 'oscservice-token', url: 'https://github.com/opensecuritycontroller/sdn-controller-nsc-plugin.git'
    		} catch (err) {
                git branch: 'master', credentialsId: 'oscservice-token', url: 'https://github.com/opensecuritycontroller/sdn-controller-nsc-plugin.git'
            }
        }

		sh 'mkdir -p osc-nuage-plugin'
        dir ('osc-nuage-plugin') {
    		try {
    			git branch: '${plugin_branch}', credentialsId: 'oscservice-token', url: 'https://github.com/opensecuritycontroller/osc-nuage-plugin.git'
    		} catch (err) {
                git branch: 'master', credentialsId: 'oscservice-token', url: 'https://github.com/opensecuritycontroller/osc-nuage-plugin.git'
            }
        }

		sh 'mkdir -p sdn-controller-nsfc-plugin'
        dir ('sdn-controller-nsfc-plugin') {
    		try {
    			git branch: '${plugin_branch}', credentialsId: 'oscservice-token', url: 'https://github.com/opensecuritycontroller/sdn-controller-nsfc-plugin.git'
    		} catch (err) {
                git branch: 'master', credentialsId: 'oscservice-token', url: 'https://github.com/opensecuritycontroller/sdn-controller-nsfc-plugin.git'
            }
        }
    }

    stage('Build Docker') {
        env.JAVA_HOME="${tool name: 'Java 8.121', type: 'jdk'}"
        env.PATH="${env.JAVA_HOME}/bin:${env.PATH}"
        dir ('osc-core') {
             sh 'ant ovf -Dimage-format='+IMG_FORMAT+ ' -Dall='+all
        }
    }

    stage('Publish') {
       dir ('osc-core') {
            buildVersion = sh(script: "ls | grep Build* | tr -d '\r' | tr -d '\n'", returnStdout: true)
            echo 'Found build: ' + buildVersion
            sh 'sudo rm ' + outputDir + buildVersion + ' -rf'
            sh 'sudo mv -v '+buildVersion+' ' + outputDir
            echo "http://10.3.240.52/" + "${branch}" + "-all/" + "${buildVersion}/"
       }
    }

    //stage('Clean old builds') {
      //  sh 'cd '+outputDir+'; sudo rm `ls '+outputDir+' -t | awk \'NR>3\'` -rf'
//    }

    stage('Clean workspace') {
        sh 'sudo rm * -rf'
    }

}
